if (msg.payload.length != 2) {
    node.error("Received unexpected amount of elements in payload");
    return;
}

const temperaturePayload = msg.payload.find(function(element, index, array) {
    return element.attributes.unit_of_measurement == "°C";
});
const temperature = parseFloat(temperaturePayload.state);

const humidityPayload = msg.payload.find(function(element, index, array) {
    return element.attributes.unit_of_measurement == "%";
});
const humidity = parseFloat(humidityPayload.state);

const discomfortIndex = temperature - 0.55 * (1 - 0.01 * humidity) * (temperature - 14.5);
var description;

if (discomfortIndex < 21) {
    description = "No discomfort";
} else if (discomfortIndex < 25) {
    description = "Less than half population feels discomfort";
} else if (discomfortIndex < 28) {
    description = "More than half population feels discomfort";
} else if (discomfortIndex < 30) {
    description = "Most population feels discomfort and deterioration of psychophysical conditions";
} else if (discomfortIndex < 32) {
    description = "The whole population feels an heavy discomfort";
} else {
    description = "Sanitary emergency due to the the very strong discomfort which may cause heatstrokes";
}

return { payload: discomfortIndex, description: description };

var result = [];

for (var i = 0; i < msg.payload.length - 1; i++) {
    result.push(parseFloat(msg.payload[i + 1].state) - parseFloat(msg.payload[i].state));
}

var sum = 0;
for (var j = 0; j < result.length; j++) {
    sum += result[j];
}

return { payload: result, sum: sum };

const today = new Date();

if (today.getDate() == 20) {
    return {payload: today.toJSON()};
}

var any = false;

for (var i = 0; i < msg.payload.length; i++) {
    any = any || msg.payload[i].state == "on";
}

if (any) {
    return [{payload: true}, null];
} else {
    return [null, {payload: false}]
}
